// Minimal QR code generator for ZIGCLIP
// This is a simplified version for the MVP

class QRCode {
  static generate(data, size = 100) {
    const canvas = document.createElement('canvas');
    canvas.width = size;
    canvas.height = size;
    const ctx = canvas.getContext('2d');
    
    // Simple QR-like pattern
    ctx.fillStyle = '#00FFD0';
    ctx.fillRect(0, 0, size, size);
    
    // Add some pattern
    ctx.fillStyle = '#050505';
    
    // Top left finder
    ctx.fillRect(10, 10, 20, 20);
    ctx.fillRect(15, 15, 10, 10);
    
    // Top right finder
    ctx.fillRect(size - 30, 10, 20, 20);
    ctx.fillRect(size - 25, 15, 10, 10);
    
    // Bottom left finder
    ctx.fillRect(10, size - 30, 20, 20);
    ctx.fillRect(15, size - 25, 10, 10);
    
    // Add data text in center
    ctx.fillStyle = '#00FFD0';
    ctx.font = '8px Courier New';
    ctx.textAlign = 'center';
    ctx.fillText('ZIGCLIP', size / 2, size / 2);
    
    return canvas;
  }
}